ProtocolApp.VisitController = Ember.ObjectController.extend({
    needs: "protocolsConfigure",
    actions: {
        drop_procedure_into_visit: function(a) {
            var b = this.get("controllers.protocolsConfigure").get("procedures").findBy("id", a);
            if (!this.model.get("procedureIds")) this.model.set("procedureIds", [ b.id ]); else this.model.get("procedureIds").pushObject(b.id);
        }
    },
    procedures: function() {
        var a = this.model.get("procedureIds");
        if (!a) return [];
        return this.get("controllers.protocolsConfigure").get("procedures").filter(function(b) {
            return a.contains(parseInt(b.id));
        });
    }.property("model.procedureIds.@each")
});