App = Em.Application.create({
    rootElement: $(".view-container")
}), App.Router.map(function() {
    this.resource("subjects", function() {
        this.resource("subject", {
            path: "/:id"
        }, function() {
            this.route("edit");
        }), this.route("add");
    });
}), App.IndexRoute = Ember.Route.extend({
    model: function() {
        return "subjects";
    },
    afterModel: function(a) {
        this.transitionTo(a);
    }
}), App.SubjectsRoute = Ember.Route.extend({
    model: function() {
        return this.store.find("subject");
    }
}), App.SubjectRoute = Ember.Route.extend({
    model: function(a) {
        return this.store.find("subject", a.id);
    }
}), App.ShellRestAdapter = DS.RESTAdapter.extend({
    pathForType: function(a) {
        return a;
    }
}), App.ApplicationAdapter = DS.RESTAdapter.extend({
    host: "http://localhost:8082/api"
}), App.Store = DS.Store.extend({}), App.Subject = DS.Model.extend({
    name: DS.attr("string"),
    age: DS.attr("number")
}), App.Subject.reopenClass({
    FIXTURES: [ {
        id: 1,
        name: "Jorge",
        age: 25
    }, {
        id: 2,
        name: "Javier",
        age: 30
    } ]
}), App.SubjectsAddController = Ember.ObjectController.extend({
    actions: {
        add: function() {
            var a = this.store.createRecord("subject", {
                name: this.get("name"),
                age: this.get("age")
            });
            a.save(), this.transitionToRoute("subjects");
        }
    }
}), App.SubjectEditController = Ember.ObjectController.extend({
    actions: {
        update: function() {
            var a = this.get("model");
            a.save(), this.transitionToRoute("subject", a);
        }
    }
});